function binarySearch(arr: number[], item: number) {
    // В переменных Low и high хранятся границы той части списка, в которой выполняется поиск
    let low: number = 0;
    let high: number = arr.length - 1;
    // Пока эта часть не сократится до одного элемента, проверяем средний элемент
    while (low <= high) {
        let mid = Math.floor((low + high) / 2);
        let guess = arr[mid];
        if (guess === item) return mid;
        if ((guess as number) > item) high = mid - 1;
        else low = mid + 1;
    }
    return null;
}
